#' Rasch Mixture Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param nc .
#' @param step .
#' @param type .
#' @param aic .
#' @param bic .
#' @param caic .
#' @param imean .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param pbis .
#' @param average .
#' @param pclass .
#' @param iplot .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$item$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$item$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$person$average} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$person$persons} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$iplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
mixture <- function(
    data,
    vars,
    nc = 2,
    step = 1,
    type = "RSM",
    aic = TRUE,
    bic = TRUE,
    caic = FALSE,
    imean = TRUE,
    imeasure = TRUE,
    ise = FALSE,
    infit = FALSE,
    outfit = FALSE,
    pbis = FALSE,
    average = FALSE,
    pclass = FALSE,
    iplot = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('mixture requires jmvcore to be installed (restart may be required)')

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- mixtureOptions$new(
        vars = vars,
        nc = nc,
        step = step,
        type = type,
        aic = aic,
        bic = bic,
        caic = caic,
        imean = imean,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        pbis = pbis,
        average = average,
        pclass = pclass,
        iplot = iplot)

    analysis <- mixtureClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
