#' Rasch Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param step .
#' @param type .
#' @param aic .
#' @param bic .
#' @param caic .
#' @param imean .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param pbis .
#' @param wrightmap .
#' @param inplot .
#' @param outplot .
#' @param angle a number from 0 to 45 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$item$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$item$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$total} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pmeasure} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pse} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pinfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$poutfit} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' @export
rasch <- function(
    data,
    vars,
    step = 1,
    type = "RSM",
    aic = TRUE,
    bic = TRUE,
    caic = TRUE,
    imean = FALSE,
    imeasure = FALSE,
    ise = FALSE,
    infit = FALSE,
    outfit = FALSE,
    pbis = FALSE,
    wrightmap = FALSE,
    inplot = FALSE,
    outplot = FALSE,
    angle = 0) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rasch requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- raschOptions$new(
        vars = vars,
        step = step,
        type = type,
        aic = aic,
        bic = bic,
        caic = caic,
        imean = imean,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        pbis = pbis,
        wrightmap = wrightmap,
        inplot = inplot,
        outplot = outplot,
        angle = angle)

    analysis <- raschClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

