                columns=list(
                    list(
                        `name`="name", 
                        `title`="Item", 
                        `type`="text", 
                        `content`="($key)"),
                    list(
                        `name`="item", 
                        `title`="Z statistic", 
                        `type`="number"),
                    list(
                        `name`="p", 
                        `title`="p", 
                        `format`="zto, pvalue"))))
            self$add(jmvcore::Image$new(
                options=options,
                name="plot4",
                title="Person fit plot",
                requiresData=TRUE,
                visible="(plot4)",
                width=600,
                height=400,
                renderFun=".plot4",
                refs="snowRMM",
                clearWith=list(
                    "vars")))
            self$add(jmvcore::Image$new(
                options=options,
                name="plot5",
                title="Rasch residual factor plot",
                requiresData=TRUE,
                visible="(plot5)",
                width=500,
                height=500,
                renderFun=".plot5",
                refs="pairwise",
                clearWith=list(
                    "vars")))
            self$add(jmvcore::Preformatted$new(
                options=options,
                name="text",
                title="Rasch residual factor analysis"))
            self$add(jmvcore::Preformatted$new(
                options=options,
                name="text1",
                title="Q3 fit statistic based on the residuals"))}))

raschBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "raschBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowRMM",
                name = "rasch",
                version = c(1,0,0),
                options = options,
                results = raschResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Rasch Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param num .
#' @param step .
#' @param type .
#' @param fit .
#' @param rel .
#' @param imean .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param pbis .
#' @param rsm .
#' @param pcm .
#' @param thr .
#' @param wrightmap .
#' @param lrsplit .
#' @param mlsplit .
#' @param waldsplit .
#' @param lr .
#' @param ml .
#' @param ml1 .
#' @param mlsplit1 .
#' @param wald .
#' @param gofplot .
#' @param tlab .
#' @param inplot .
#' @param outplot .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param piplot .
#' @param plot4 .
#' @param plot5 .
#' @param res .
#' @param q3 .
#' @param res1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$item$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$item$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rel} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$thr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rsm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pcm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$piplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$gofplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$ptotal} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pmeasure} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pse} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pinfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$poutfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$lr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ml} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ml1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$wald} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$rel$asDF}
#'
#' \code{as.data.frame(results$rel)}
#'
#' @export
rasch <- function(
    data,
    vars,
    num = 1,
    step = 1,
    type = "RSM",
    fit = TRUE,
    rel = TRUE,
    imean = TRUE,
    imeasure = TRUE,
    ise = TRUE,
    infit = FALSE,
    outfit = FALSE,
    pbis = FALSE,
    rsm = FALSE,
    pcm = FALSE,
    thr = FALSE,
    wrightmap = TRUE,
    lrsplit = "median",
    mlsplit = "median",
    waldsplit = "median",
    lr = FALSE,
    ml = FALSE,
    ml1 = FALSE,
    mlsplit1 = "median",
    wald = FALSE,
    gofplot = FALSE,
    tlab = "item",
    inplot = FALSE,
    outplot = FALSE,
    angle = 0,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    piplot = FALSE,
    plot4 = FALSE,
    plot5 = FALSE,
    res = "stdr",
    q3 = FALSE,
    res1 = "stdr") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rasch requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- raschOptions$new(
        vars = vars,
        num = num,
        step = step,
        type = type,
        fit = fit,
        rel = rel,
        imean = imean,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        pbis = pbis,
        rsm = rsm,
        pcm = pcm,
        thr = thr,
        wrightmap = wrightmap,
        lrsplit = lrsplit,
        mlsplit = mlsplit,
        waldsplit = waldsplit,
        lr = lr,
        ml = ml,
        ml1 = ml1,
        mlsplit1 = mlsplit1,
        wald = wald,
        gofplot = gofplot,
        tlab = tlab,
        inplot = inplot,
        outplot = outplot,
        angle = angle,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        piplot = piplot,
        plot4 = plot4,
        plot5 = plot5,
        res = res,
        q3 = q3,
        res1 = res1)

    analysis <- raschClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

